        //读写数据
        byte[] b = new byte[1024];
        int len;
        while((len = fis.read(b)) != -1){
            fos.write(b,0,len);
        }
        //关闭资源
        fos.close();
        fis.close();
}


流的关闭原则：先开后关，后开先关。

